====DBufInfo dbi====

<pre>
struct DBufInfo
	STRUCTURE DBufInfo,0
	APTR	dbi_Link1
	ULONG	dbi_Count1
	STRUCT	dbi_SafeMessage,MN_SIZE
	APTR	dbi_UserData1
	APTR	dbi_Link2
