== Old code ==
<pre lang="c">
/* Calculation of simple interest */
/* Author gekay Date: 25/05/2004 */
main( )
{
int p, n ;
float r, si ;
p = 1000 ;
n=3;
r = 8.5 ;
/* formula for simple interest */
si = p * n * r / 100 ;
printf ( "%f" , si ) ;
}

</pre>

== New code ==
<pre lang="c">
/*Calculation of simple interest*/
/*Author : Subin Date : 15/4/2010*/

int main( )  {
   int p, n ;
   float r, si ;
   p = 1000 ;
   n = 3 ;
   r = 8.5 ;
   /* formula for simple interest */
   si = p * n * r / 100 ;
   printf ( "%f" , si ) ;
   return 0;
}
</pre>
