static void InitMenus(void)
{
    struct NewMenu *actnm = nm;

    for(actnm = nm; actnm->nm_Type != NM_END; actnm++)
    {
	if (actnm->nm_Label != NM_BARLABEL)
	{
	    ULONG  id = (IPTR)actnm->nm_Label;
	    CONST_STRPTR str = MSG(id);

	    if (actnm->nm_Type == NM_TITLE)
	    {
