	glEnable(GL_LIGHT0);
	float col[]={1.0,1.0,1.0,1.0};
	glLightfv(GL_LIGHT0,GL_DIFFUSE,col);
	initskybox();
}

coordinate p1(-5.0,5.0,-5.0);
