jQuery(document).ready(function($) {
  $('<form><label>Filter: </label><input type="text"></input></form>').insertBefore('table.filter').each( function() {
    $('input', this).keyup( function() {
      var words = $(this).val().toLowerCase().split(" ");
      $(this).parent().next('table.filter').find('> tbody > tr, tr').not(':has(th)').each( function() {
        var $row = $(this), row_t = $row.text().toLowerCase();
        for (var word = 0; word < words.length; ++word) {
          if (row_t.indexOf(words[word]) >= 0) {
            $row.show();
          } else {
            $row.hide();
          }
        }
      });
    }).parent().submit( function() { return false; } );
  });
});

