== perbook css and js ==

Could someone put these codes to [[MediaWiki:Perbook/Turkish.js]] and [[MediaWiki:Perbook/Turkish.css]] pages, please?
<syntaxhighlight lang="jquery">
/*
   Tooltip for use in [[Template:Turkish/Tooltip]]
*/ 
$(document).ready(function() {
   $(".maintext").hover( function() {
      if (!$(this).parents().hasClass("tbox")) {
         var pos = $(this).position(); // position of the 'maintext'
         $(this).wrap('<div class="tbox" />'); // make 'maintext' a part of 'tbox'
         var tbox = $(this).parent(".tbox");
         $(this).data('title',$(this).attr('title')); 
         var tooltip = $(this).data("title");
         $(this).removeAttr('title'); // remove title to prevent native tooltip
         tbox.append('<div class="tooltip">' + tooltip + "</div>"); // add the tooltip text
         tbox.css( { "display": "block", "left": (pos.left-13) + "px", "top": (pos.top-9) + "px" } ); // set the position of the 'tbox'
         $(this).clone().insertBefore(tbox).attr("id", "ctext"); // place a clone of the 'maintext' as a placeholder
      }
   }, function() {
      var tbox = $(this).parent(".tbox");
      var clone = tbox.prev("#ctext"); 
      clone.remove(); //remove clone
      $(this).insertBefore(tbox); //replace 'maintext'
      var tbox = $(this).siblings(".tbox"); //tbox is no longer parent
      tbox.remove();
      $(this).attr('title',$(this).data("title")); //replace 'title' for another hover event
   } );
} );
</syntaxhighlight>
<br />
<syntaxhighlight lang="css">

/* 
   Style codes for tooltip
*/
.maintext
{
  color: #222;
  font-weight: bold;
  display: inline-block;
}

.maintext2 {
  color: #222;
  font-weight: bold;
  display: inline-block;
}
.tbox {
  position: absolute;
  display: none;
  border: 1px solid #bebebe; 
  background: #f0f0f0;
  border-radius: 5px;
  cursor: pointer;
  padding: 4px;
  box-shadow: 2px 2px 7px #ccc;
  -moz-box-shadow: 2px 2px 7px #ccc;
  -webkit-box-shadow: 2px 2px 7px #ccc;
  vertical-align: top;
}

.tbox .tooltip
{
  border: none;
  display: inline-block;
  padding: 2px 5px;  
  font-size: 85%;
  text-align: justify;
  max-width: 250px;
  vertical-align: middle;
}


.tbox .maintext{
  vertical-align: top;
  padding: 3px 7px 3px 7px;
  font-weight: bold;
  border: 1px solid #bebebe; 
  background: #f9f9f9;
  box-shadow: inset 2px 2px 10px 0px #ccc;
  -moz-box-shadow: inset 2px 2px 10px 0px #ccc;
  -webkit-box-shadow: inset 2px 2px 10px 0px #ccc;
  border-radius: 5px;
  -moz-border-radius: 5px;
  -webkit-border-radius: 5px;
}
</syntaxhighlight>
