                Zy = 2 * Zx * Zy + Cy;
                Zx = Zx2 - Zy2 + Cx;
                Zx2 = Zx * Zx;
                Zy2 = Zy * Zy;
                i += 1.0;
        }
        return i;
}

double 
max(double a, double b)
{
        if (a > b)
                return a;
        else
                return b;
}

