Function GetPlayerName : String;
  Var
    PlayerName : String;
  Begin
    Writeln;
    repeat
       Write('Please enter your name: ');
       Readln(PlayerName);
       Writeln;
       if PlayerName = '' then
          writeln('That is an invalid input.');
    until PlayerName <> '';
    GetPlayerName := PlayerName;
  End;
