        Using reader As StreamReader = File.OpenText("Recentscores.txt")
            Dim line As String
            For j As Integer = 1 To 3
                line = reader.ReadLine()
                RecentScores(j).Name = line
                line = reader.ReadLine()
                RecentScores(j).Score = line
            Next
        End Using
    End Sub
