def SaveScores(RecentScores):
  currentFile = open("recentScores.txt","w")
  count = 1
  for count in range(1,NO_OF_RECENT_SCORES+1):
    LineToAddToFile = RecentScores[count].Name + "," + str(RecentScores[count].Score) + "\n"
