Function GetSuit(SuitNo : Integer) : String;
  Var
    Suit : String;
  Begin
    Suit := '';
    Case SuitNo Of
      1 : Suit := 'Clubs';
      2 : Suit := 'Diamonds';
      3 : Suit := 'Hearts';
      4 : Suit := 'Spades';
      5 : Suit := 'Swords';  // new suit
    End;
    GetSuit := Suit;
  End;

