/**
 * Add a "My sandbox" link to the personal portlet menu.
 * Dependencies: mediawiki.util, mediawiki.Title, mediawiki.Uri
 *
 * @source mediawiki.org/wiki/Snippets/MySandbox
 * @version 2
 */
( function ( mw, $ ) {
	$( document ).ready( function () {
		var conf, title, url;

		// Costomize/Translate this to your needs
		conf = {
			subpageName: 'sandbox',
			portletLabel: 'Sandbox',
			portletTooltip: 'Go to your sandbox',
			editintroPagename: 'Template:User_sandbox',
			preloadPagename: 'Template:User_sandbox/preload'
		};

		// Don't alter the code below
		// Use Special:MyPage (as opposed to mw.user.getName()) so that it
		// works for logged-out users as well.
		title = new mw.Title( 'Special:MyPage/' + conf.subpageName );
		url = new mw.Uri( title.getUrl() );
		url.extend({
			action: 'edit',
			redlink: 1,
			editintro: new mw.Title( conf.editintroPagename ),
			preload: new mw.Title( conf.preloadPagename )
		});

		mw.util.addPortletLink(
			'p-personal',
			url,
			conf.portletLabel,
			'pt-mysandbox',
			conf.portletTooltip,
			null,
			'#pt-preferences'
		);
	});
}( mediaWiki, jQuery ) );

/**
 * @source en.wikinews.org/wiki/MediaWiki:Gadget-dictionaryLookupHover.js
 * 
 * Note this script can take a number of parameters. Parameters are stored in the wiktLookup object.
 * Generally its a good idea to set parameters before the onload event is fired (some parameters it
 * matters, others it doesn't). An example of parameters would be using the following js code (don't
 * feel like you have to set all of them. they all have defaults. this is just an example):
 * 
 * if (!window.wiktLookup) wiktLookup = {};
 * wiktLookup.count = 1; // number of defn to return.
 * wiktLookup.showWord = 'bold'; // to show what word we're looking up. one of bold, none, link
 * wiktLookup.noRedir = false; // true to stop magic (not mediawiki)-redirection. unrecomended
 * wiktLookup.audio = true; // true, false, autoplay. NOT IMPLEMENTED
 * wiktLookup.width = 500; // width of box. make sure number not string. in units of pixels.
 * wiktLookup.height = 400; //- height of box
 * wiktLookup.key = 'w';// - ctrl+shift+key makes box popup (should be lowercase. default is 'l')
 * wiktLookup.reverseShift = false; // - if set, requires shift+double click to lookup a word.
 * wiktLookup.mode = 'context'; // - context, bottom. default to context. to display as tooltip, or at bottom
 * wiktLookup.definitionBegin = 'Definition ('; // - for i18n. in english it is 'Definition ('. used in mode='bottom'
 * wiktLookup.definitionEnd = '):'; //	- for i18n. in English it is '):' used in bottom mode
 * wiktLookup.hideText = 'hide'; //- for i18n. in English it is 'hide'. used in bottom mode.
 * wiktLookup.exit = true; // have a hide link.
 * wiktLookup.disableByDefault = true; // enable only in <span class="wiktLookup-enable">blah</span> sections.
**/

/*global window, document, jQuery, mediaWiki */
/*jslint browser: true, undef: true, nomen: true, eqeqeq: true, bitwise: true, newcap: true, immed: true, maxerr: 80 */

(function($, mw) {
	var user_lang, content_lang;

	// set wiktLookup.useNewWindow to true to force new window.
	window.wiktLookup = window.wiktLookup || {};
	wiktLookup.width = wiktLookup.width || 420;
	wiktLookup.height = wiktLookup.height || '180';
	wiktLookup.key = wiktLookup.key || 'l';
	wiktLookup.validLangs = {
		en: "en", fr: "fr", frc: "fr", fra: "fr", fre: "fr", nl: "nl",
		es: 'es', it: 'it', ja: 'ja', pt: 'pt', ru: 'ru'
	};
	wiktLookup.definitionBegin = wiktLookup.definitionBegin || 'Definition (';
	wiktLookup.definitionEnd = wiktLookup.definitionEnd || '):';
	wiktLookup.hideText = wiktLookup.hideText || 'hide';
	wiktLookup.disableByDefault = wiktLookup.disableByDefault || false;
	wiktLookup.stripAfterDash = /-.*$/;
	wiktLookup.url = '//upload.wikimedia.org/wikipedia/commons/thumb/e/ec/Wiktionary-logo.svg/840px-Wiktionary-logo.svg.png';

	user_lang = mw.config.get('wgUserLanguage', '').replace(wiktLookup.stripAfterDash);
	content_lang = mw.config.get('wgContentLanguage');

	wiktLookup.wiktDomain = wiktLookup.validLangs[user_lang]
		|| wiktLookup.validLangs[content_lang.replace(stripAfterDash)]
		|| 'en';

	wiktLookup.preferLang = wiktLookup.preferLang || content_lang || wiktLookup.wiktDomain;

	// Ranges: http://www.quirksmode.org/dom/range_intro.html
	wiktLookup.selection = function() {
		var what, range;

		if (window.getSelection) {
			what = window.getSelection();
			if (what.getRangeAt) {
				range = what.getRangeAt(0);
				range = range.cloneRange();
				if (range.startOffset !== range.endOffset  ) {
					range.setStart(range.startContainer, range.startOffset);
					range.setEnd(range.endContainer, range.endOffset);
				}
			} else if (document.createRange) {
				range = document.createRange();
				if (what.startOffset !== what.endOffset  ) {
					range.setStart(what.startContainer, what.startOffset);
					range.setEnd(what.endContainer, what.endOffset);
				}
			}
		} else if (document.selection && document.selection.createRange) {
			range = document.selection.createRange();
		} else {
			return { html: $(""), text: "" };
		}
		if (range.commonAncestorContainer) {
			what = range.commonAncestorContainer;
			if (what.nodeType === 3) {
				what = what.parentNode;
			}
			what = { html: $(what), text: range.toString() };
		} else if (range.parentElement) {
			what = range.parentElement;
			if (what.nodeType === 3) {
				what = what.parentNode;
			}
			what = { html: $(what), text: range.text };
		}
		if (range.detach) {
			range.detach();
		}
		return what;
	};

	wiktLookup.findWord = function (e) {
		var selection,
			enabled,
			foundAnEnableSection,
			foundLangSection,
			$cur,
			langOverride,
			i = 10;

		// Don't do anything when shift key is pressed unless reverseShift is true
		if ((e.shiftKey && !wiktLookup.reverseShift) || (!e.shiftKey && wiktLookup.reverseShift)) {
			return true;
		}

		selection = wiktLookup.selection();

		if (selection.html.length === 0) {
			return;
		}

		// determine if we're in an enabled section
		// aka <span class="wiktLookup-enable">blah</span>
		enabled = !wiktLookup.disableByDefault;
		foundAnEnableSection = false;
		foundLangSection = false;
		$cur = selection.html;

		while ( --i > 0 && ($cur = $cur.parent()).length ) {
			if (!foundAnEnableSection && $cur.hasClass('wiktLookup-disable')) {
				return;
			}
			if (!foundAnEnableSection && $cur.hasClass('wiktLookup-enable')) {
				enabled = true;
				foundAnEnableSection = true;
				if (foundLangSection) {
					break;
				}
			}
			if (!foundLangSection && $cur.attr('lang')) {
				langOverride = $cur.attr('lang').replace(wiktLookup.stripAfterDash);
 				if (foundAnEnableSection) {
 					break;
 				}
 				foundLangSection = true;
			}
		}

		if (!enabled) {
			return;
		}

		e.preventDefault();
		e.stopPropagation();
		return wiktLookup.lookupWord(selection.text, e.pageX, e.pageY, langOverride);
	};

	wiktLookup.getInnerWidth = function() {
		// standard
		if (window.innerWidth !== undefined) {
			return window.innerWidth;
		}
		// IE 6 in standards mode
		if (document.documentElement && document.documentElement.clientWidth !== undefined) {
			return document.documentElement.clientWidth;
		}
		// other IE
		if (document.body && document.body.clientWidth !== undefined) {
			return document.body.clientWidth;
		}
		return 0;
	};

	wiktLookup.lookupWord = function(word, x, y, langOveride) {
		var word2, alph_regexp = "[\\wa-zA-Zа-яА-Яà-žÀ-ŽΑ-ῥԱ-ևぁ-ヶ促-杁ㄱ-하ⁿßſ]";

		alph_regexp = new RegExp( "(?:^" + alph_regexp + "|" + alph_regexp + "\\s?$)" );

		if ( !word.match(alph_regexp) ) {
			// this regex doesn't work if word starts or ends with accents.
			// is this regex even needed?
			word2 = word.match(/\b(?:[\S]|(?!\.\s)\.)*\b/); // strip quotation marks
			word = word2 ? word2[0] : word;
		}

		if (!word) {
			return false;
		}

		$.getJSON('//' + wiktLookup.wiktDomain + '.wiktionary.org/w/api.php?callback=?', {
			'action':		'parse',
			'redirects':	true,
			'prop':			'text',
			'format':		'json',
			'disabletoc':	true,
			'disablepp':	true,
			'page':			encodeURIComponent(word),
			'count':		wiktLookup.count,
			'lang':			langOveride || wiktLookup.preferLang,
			'showWord':		wiktLookup.showWord,
			'rd':			500
		}).done(function(api) {
			var warnings, parse, xml = '';

			if (api.parse === undefined) {
				return;
			}

			xml += '<?xml version="1.0"?>';
			xml += '<api>';

			if ('warnings' in api) {
				warnings = api.warnings;
				xml += '<warnings>';
				if ('main' in warnings) {
					xml += '<main xml:space="preserve">';
					xml += ( warnings.main['*'] || '' );
					xml += '</main>';
				}
				xml += '</warnings>';
			}

			parse = api.parse;

			if ('title' in parse) {
				xml += '<parse title="' + parse.title + '">';
				if ('text' in parse) {
					xml += '<text xml:space="preserve">';
					xml += ( parse.text['*'] || '' );
					xml += '</text>';
				}
				xml += '</parse>';
			}

			xml += '</api>';

			$.getJSON('//' + wiktLookup.wiktDomain + '.wiktionary.org/w/api.php?callback=?', {
				'action':	'query',
				'prop':		'revisions',
				'rvprop':	'content',
				'format':	'json',
				'titles':	'MediaWiki:ExtractFirst.xsl'
			}).done(function(api) {
				var xsl, xsltProcessor, left, top = y+10;

				api = api && api.query && api.query.pages;
				if ( api === undefined ) {
					return;
				}

				$.each( api, function() {
					xsl = this.revisions && this.revisions[0]['*'];
					return false;
				});

				if ( xsl === undefined ) {
					return;
				}

				xml = $.parseXML(xml);
				xsl = $.parseXML(xsl);

				if (window.XSLTProcessor) {
					xsltProcessor = new XSLTProcessor();
					xsltProcessor.importStylesheet(xsl);
					xml = xsltProcessor.transformToFragment(xml, document);
				} else if (xml.transformNode) {
					xml = $.parseXML(xml);
					xml = xml.transformNode(xsl);
				}

				left = wiktLookup.getInnerWidth();
				left = (x + wiktLookup.width > left ? left - wiktLookup.width : x );

 				$('#dict-popup-frame').html(xml).css({
 					'top': top + 'px',
 					'left': left + 'px'
 				});
 				$('#dict-popup-container').show();
			});
		});
	};

	mw.hook( 'wikipage.content').add(function($content) {
		var $div;

		$(document).dblclick(wiktLookup.findWord).keypress(function(e) {
			var key;

			if ( !e.ctrlKey || !e.shiftKey ) {
				return true;
			}

			key = String.fromCharCode(e.which);

			if (key === wiktLookup.key || key === wiktLookup.key.toUpperCase()) {
				wiktLookup.findWord(e);
				e.preventDefault();
				return false;
			}
			return true;
		});

		$div = $('<div id="dict-popup-container"></div>');
		$frame = $('<div id="dict-popup-frame"></div>').css({
			'border':	'none',
			'z-index':	102,
			'height':	wiktLookup.height + 'px'
		});

		if (wiktLookup.mode !== 'bottom') {
			$frame.css({ 'position': 'absolute', 'width': wiktLookup.width + 'px' });
		} else {
			$frame.css('width', '98%');
		}

		if (wiktLookup.mode === 'bottom') {
			$div.css({
				'padding':	'0.3em',
				'position':	'fixed',
				'margin':	0,
				'bottom':	0,
				'z-index':	50,
				'width':	'100%',
				'border-top':	'solid thin black',
				'background':	'url(' + wiktLookup.url + ') white no-repeat 30%'
			}).append(
				wiktLookup.definitionBegin,
				$('<a href="#"></a>').append(wiktLookup.hideText).click(wiktLookup.hide),
				wiktLookup.definitionEnd
			);
		}

		$div.hide(1).append($frame);
		$content.append($div);
	});

	wiktLookup.hide = function(e) {
		$('#dict-popup-container').hide();
	};

	$(document).click(wiktLookup.hide);
})(jQuery, mediaWiki);

