		{

			// Test.xls is in the C:\

			string filePath = @"D:\BPT_Project\Bpt_Bulk_Upload_Files\Book1.xls";

			string connectionString = "Provider=Microsoft.Jet.OleDb.4.0;Data Source=" + filePath + ";";

			connectionString += "Extended Properties=Excel 8.0;";

			// always read from the sheet1.

			OleDbCommand myCommand = new OleDbCommand("Select * from [Sheet3$];");

			OleDbConnection myConnection = new OleDbConnection(connectionString);

			myConnection.Open();

			myCommand.Connection = myConnection;

			OleDbDataReader myReader = myCommand.ExecuteReader();

			while (myReader.Read())
			{

				for (int i = 0; i < 5; i++)
				{
					Response.Write(myReader.GetValue(i).ToString ()+ " ");
				}
				Response.Write("<br>");
			}
			myConnection.Close();
		}


	}
}


'''Sorting Algorithm'''

