function QueryStringSpan()
{
  var queries = window.location.search.substr(1).split('&');

  for (i = 0; i < queries.length; i++) {
    var query = queries[i].split('=');
    var query_test = document.getElementById('query_' + query[0]);
    if (query_test) 
      query_test.innerHTML = query[1];
  }
}

addOnloadHook(QueryStringSpan);

