var rt = {};
rt.span = "WKRTSpan";
rt.iframe = null;
rt.pairs = null;
rt.getIFrame = function() {
  if(rt.iframe == null) rt.iframe = wk.makeElement("iframe", {width:"100%", height:"550px"});
  return rt.iframe;
}
rt.talkLinks = function() {
   var frame = wk.makeElement("div");
   for(i = 0; i < rt.pairs.length; i++) {
       var nameplace = rt.pairs[i].split("@");
       var link = rt.createLink(nameplace[0], nameplace[1]);
       frame.appendChild(link);
       frame.appendChild(document.createTextNode(" — "));
   }
   link = wk.makeElement("a", {href:"#" + rt.span}, "Remove");
   link.onclick = rt.initFrame;
   frame.appendChild(link);
   return frame;
}
rt.createLink = function(username, project) {
  if(username == "") username = wgUserName;
  if(project  == "") project  = "wikibooks";
  username = username.replace(" ", "_");
  var link = wk.makeElement("a", {href:"#" + rt.span}, project);
  link.onclick = function(){ rt.getTalkPage(project, username)};
  return link;
}
rt.getTalkPage = function (project, username) {
  var talkurl = 
    "http://" + project + ".org" +
    "/w/index.php?title=User_talk:" + username +
    "&action=view&printable=yes";
  var frame = wk.spanText(rt.span, "");
  var iframe = rt.getIFrame();
  iframe.setAttribute("src", talkurl);
  frame.appendChild(rt.talkLinks());
  frame.appendChild(iframe);
  frame.appendChild(rt.talkLinks());
}
rt.initFrame = function() {
  var span = document.getElementById(rt.span);
  var items = span.innerHTML;
  if(rt.pairs == null) rt.pairs = items.split(",");
  span.innerHTML = "";
  if(span != null) {
    span.style.display = "block";
    span.appendChild(rt.talkLinks());
  }
}
addOnloadHook(rt.initFrame);

var BookName, BookNameE;
var gml_xmlhttp;
var res;

function WKPrepPV(form) {
  BookName = form.WKPVBookName.value;
  if(BookName == "") {
    return;
  }
  BookNameE = BookName.replace(" ", "_");
  res = document.getElementById('WKViewerResults');
  if(res) {
    gml_xmlhttp = HTTPClient();
    if (!gml_xmlhttp) {
      alert("xml thingy does not work");
      return;
    }
    gml_xmlhttp.open("GET", "http://en.wikibooks.org/w/index.php?title=" + BookNameE +
                            "&action=raw", true);
    gml_xmlhttp.onreadystatechange = WKPrepPV2;
    gml_xmlhttp.send(null);
  } else {
    alert("No WKViewerResults element!");
  }
}

function WKPrepPV2() {
  if (gml_xmlhttp.readyState != 4) {
    return;
  }
  var xml = gml_xmlhttp.responseText.replace(/</g, "&lt;");
  if(xml.length == 0) {
    res.innerHTML = "Page does not exist.";
  } else {
    res.innerHTML = "Page Text: <br><pre style=\"overflow: auto;\">" + xml + "</pre>";
  }
}

function WKCreatePVForm() {
  var form_span = document.getElementById('WKViewerForm');
  if (form_span) {
    form_span.innerHTML = "<FORM NAME=\"WKViewerForm\" ACTION=\"\" METHOD=\"GET\">" +
                          "Enter the name of a book to get: <BR>" +
                          "<INPUT TYPE=\"text\" NAME=\"WKPVBookName\" VALUE=\"\">&nbsp;" +
                          "<INPUT TYPE=\"button\" NAME=\"WKPVSubmit\" Value=\"Get\" " +
                          "onClick=\"WKPrepPV(this.form)\"><INPUT TYPE=\"button\" "+ 
                          "NAME=\"WKPVClear\" VALUE=\"Clear\" " + 
                          "onClick=\"ClearSpan('WKViewerResults')\"></FORM>";
  }
}

addOnloadHook(WKCreatePVForm);

//Print Version Creator
//Maintained by [[User:Whiteknight]]
/* 
  This script operates a semi-automagical print version creator gadget. This gadget is 
  located at [[User:Whiteknight/Print Version Gadget]]. It uses Whiteknight's Javascript
  core library to help with some of the common tasks and the AJAX-related tasks.
  Documentation and the gadget itself is located at 
  [[User:Whiteknight/Print Version Gadget]]. 
*/

var pv = {
  formspan: "WKPVFormSpan",
  textspan: "WKPVSpan",
  statspan: "WKPVStatSpan",
  boxspan:  "WKPVFormSpanSub",
  form: null,
  text: "",       //raw converted text
  htmltext: ""   //html-formatted converted text
}

pv.getForm = function() { return pv._form; }

pv.createForm = function() {
  var form = wk.spanText(pv.formspan, "");
  if(form == null) return;
  pv._editB = wk.makeButton("WKPVEditB",    "Edit",    pv.edit);
  pv._displayB = wk.makeButton("WKPVDisplayB", "Display", pv.display);
  pv._booknameB = wk.makeInput("WKPVBookNameB", "");
  pv._listB = wk.makeElement("Textarea", {name:"WKPVListB", rows:"15", cols:"70"});
  pv._spanB = wk.makeElement("span", {id:pv.boxspan}, pv._listB);
  pv._form = wk.makeElement("form", {name:"WKPVFormB", method:"GET"}, [
      pv._booknameB,
      wk.makeButton("WKPVLoadB",    "Load",    pv.loadPage),
      " ",
      wk.makeButton("WKPVShowB",    "Show",    function(){wk.toggleDisplay(pv.boxspan, "block")}),
      wk.makeButton("WKPVHideB",    "Hide",    function(){wk.toggleDisplay(pv.boxspan, "none")}),
      " ",
      wk.makeButton("WKPVClearB",   "Clear",   pv.clear),
      " ",
      wk.makeButton("WKPVConvertB", "Convert", pv.convert),
      " ",
      pv._editB,
      pv._displayB,
      pv._spanB,
      wk.makeElement("br")
  ]);
  wk.toggleDisplay(pv._spanB, "none");
  wk.toggleDisable(pv._editB, true);
  wk.toggleDisable(pv._displayB, true);
  form.appendChild(pv._form);
}
if(typeof wk == 'object' && wk.testVersion(2.4)) {
  addOnloadHook(pv.createForm);
}

pv.clear = function() {
  wk.spanText(pv.textspan, "");
  wk.spanText(pv.statspan, "");
  wk.toggleDisplay(pv.boxspan, "none");
  pv._listB.value = "";
  wk.toggleDisable(pv._editB, true);
  wk.toggleDisable(pv._displayB, true);
}

pv.bookName = "";
pv.getBookName = function() {
  pv.bookName = pv._booknameB.value;
  return pv.bookName;
}

pv.loadPage = function() {
  pv.getBookName();
  if(pv.bookName == "") {
      wk.spanText(pv.statspan, "Must enter a valid page name to load from");
      return;
  }
  wk.loadWikiText(pv.bookName, function(text){pv._listB.value = text;});
  wk.spanText(pv.statspan, "Successfully retrieved text from [[" +
    wk.wikiLinkText(pv.bookName) + 
    "]]. Click <b>Show</b> to see and edit the page text. Click <b>Convert</b> " + 
    "to create the print version."
  );
}

pv.setChapter = function (page, offset, xml) {
  page = page.replace("[[", "");
  page = page.replace("]]", "");
  var levels = page.split("/");
  var subpage = levels[levels.length - 1];
  if(subpage == "")
  {
    subpage = levels[levels.length - 2];
  }
  return ("{"+"{Print chapter heading|" + subpage + "}}\n{{:" + page + "}}\n"); 
}

pv.setSections = function (head, offset, xml) {
  head = head.replace(/=/g, "");
  head = head.replace(/^\s*/g, "");
  head = head.replace(/\s*$/g, "");
  return("{"+"{Print unit page|" + head + "}}\n\n");
}

pv.xmlFormat = function(xml) {
  xml = xml.replace(/''+/g, ""); //kill text formatting
  xml = xml.replace(/\{\{[^\}]+\}\}/g, ""); //kill templates
  xml = xml.replace(/\[\[category:[^\]]+\]\]/ig, ""); //kill categories
  xml = xml.replace(/\|[^\]\}]+\]\]/g, "]]"); //kill piped links
  xml = xml.replace(/\[\[\/([^\]]+)\/\]\]/g, "[[" + pv.bookName + "/$1]]"); //properly address relative links
  xml = xml.replace(/\[\[([^\]]+)\]\]/g, pv.setChapter); //prepare chapter entries    
  xml = xml.replace(/===([^=]+)===/g, pv.setSections);
  xml = xml.replace(/\* ?/g, ""); //kill bullets
  xml = xml.replace(/# ?/g, ""); //kill ordered lists
  xml = xml.replace(/==[^=]+==/g, ""); //kill headings
  xml = xml.replace(/\n\n\n+/g, "\n\n"); //kill extra whitespace

  xml = xml.replace(/</g, "&lt;");
  xml = xml.replace(/>/g, "&gt;");
  var header = "{{Print version notice}}\n" +
               "__NOTOC__ __NOEDITSECTION__\n" +
               "&lt;br style=\"page-break-after: always\"&gt;\n";
  if(pv.bookName != "") {
    header = header + "{{:" + pv.bookName + "/Cover}}\n" +
                      "&lt;br style=\"page-break-after: always\"&gt;\n";
  }
  xml = header + xml + "\n= GNU Free Documentation License =\n\n{{:GFDL|=}}\n\n";
  return xml;
}

pv.convert = function() {
  wk.toggleDisable(pv._editB, false);
  wk.toggleDisable(pv._displayB, false);
  wk.toggleDisplay(pv.boxspan, "none");
  pv.htmltext = pv.xmlFormat(pv._listB.value);
  pv.text = pv.htmltext.replace(/&gt;/g, ">");
  pv.text = pv.text.replace(/&lt;/g, "<");
  wk.spanText(pv.statspan, "Successfully converted text. " +
    "Click <b>Display</b> to show the current text for copy+paste. " +
    "Click <b>Edit</b> to edit and save the current text.");
  wk.toggleDisable(pv._editB, false);
  wk.toggleDisable(pv._displayB, false);
  wk.spanText(pv.textspan, "");
}

pv.display = function() {
  wk.toggleDisplay(pv.boxspan, "none");
  wk.spanText(pv.statspan, "<b>Print version text:</b><br>");
  if(pv.getBookName() != "") {
    wk.appendSpanText(pv.statspan, "Copy the text below into " +
      "[[<a href=\"/w/index.php?title=" + pv.bookName.replace(/ /g, "_") + 
      "/Print_Version&action=edit\">" + pv.bookName + "/Print Version</a>]] " +
      "<br><small><font color=\"#FF0000\" align=\"center\">Warning</font>: " + 
      "this text may need to be edited manually to remove unwanted " + 
      "templates, categories, links or odd formatting that the tool " +
      "cannot account for.</small><br>");
  }
  wk.spanText(pv.textspan, "<pre style=\"overflow: auto;\">" + pv.htmltext + "</pre>");
}

pv.edit = function() { 
  if(pv.getBookName() == "") {
    wk.spanText(pv.statspan, "Must enter a valid page name to edit");
    return;
  }  
  wk.spanText(pv.statspan, "Loading...");
  wk.toggleDisplay(pv.boxspan, "none");
  wk.loadEditPage(pv.bookName + "/Print Version", pv._edit2);
}

pv._edit2 = function(xml) {
  var edit = wk.showEditWindow(xml, pv.textspan);
  edit.summary.value = pv.bookName + 
    " Printable Version: Created by Whiteknight's Print Version Creator";
  edit.text.value = pv.text;
  wk.spanText(pv.statspan, 
    "Editing text for [[" + 
    wk.wikiLinkText(pv.bookName + "/Print Version") + 
    "]]. Edit the text below to remove any errors or artifacts " +
    "of the conversion process. Once you have made your edits, " +
    "click <b>Save Page</b>.");
}

var pt = {}
pt.formspan = "WKTestFormSpan";
pt.textspan = "WKTestSpan";
pt.bookName = "";
pt.bookNameE = "";
pt._form = null;
pt.getForm = function() {
  return pt._form;
}
pt.getBookName = function() {
  pt.bookName = pt.booknameB.value;
  pt.bookNameE = pt.bookName.replace(/ /g, "_");
  return pt.bookName;
}
pt.createForm = function() {
  var div = wk.spanText(pt.formspan, "");
  if(div == null) return;
  pt.booknameB = wk.makeInput("WKTestBookNameB", "")
  pt._form = wk.makeElement("form", {name:"WKTestForm", method:"GET"}, [
    "Page Name:",
    pt.booknameB,
    wk.makeButton("WKTestSubmitB", "Get",   pt.prepTest),
    wk.makeButton("WKTestClearB",  "Clear", function(){wk.spanText(pt.textspan)})
  ]);
  div.appendChild(pt._form);
}
addOnloadHook(pt.createForm);
pt.prepTest = function() {
  if(pt.getBookName() == "") return;
  wk.spanText(pt.textspan, "Loading Page...");
  wk.loadWikiText(pt.bookName, pt._prepTest2);
}
pt._prepTest2 = function(text) {
  var len = text.length;
  if(len == 0) {
     wk.spanText(pt.textspan, "Page [[" + wk.wikiLinkText(pt.bookname) + "]] does not exist");
     return;
  }
  if(len >= 9 && text.substring(0, 9).toLowerCase() == "#redirect")
  {
    var dest = text.substring(10);
    dest = dest.replace("[[", "");
    dest = dest.replace("]]", "");
    res.innerHTML = "Page redirects to [[" + wk.wikiLinkText(dest) + "]]";
    return;
  }
  var words = text.match(/\S+/g).length;

  var links;
  var links_len = 0;
  if(text.match(/\[\[[^\]]+\]\]/)) {
    links = text.match(/\[\[([^\]]+)\]\]/g);
    for(i = 0; i < links.length; i++) {
      links[i] = links[i].replace(/\|[^\]]+\]\]/, "");
    }
    links_len = links.length;
  }

  var cats;
  var cats_len = 0;
  if(text.match(/\[\[category:[^\]]+\]\]/i)) {
    cats = text.match(/\[\[category\:([^\]]+)\]\]/ig);
    cats_len = cats.length;
  }

  var tempsa;
  var temps = [];
  var temps_len = 0;
  if(text.match(/\{\{[^\}]+\}\}/)) {
    tempsa = text.match(/\{\{([^\}]+)\}\}/g);
    tempsa.sort();
    for(i = 0; i < tempsa.length; i++) {
      tempsa[i] = tempsa[i].replace(/{{/, "");
      tempsa[i] = tempsa[i].replace(/}}/, "");
      tempsa[i] = tempsa[i].replace(/\|[^\}]+$/, "");
      if(tempsa[i].match(/^\/.+\/$/)) {
        tempsa[i] = tempsa[i].replace(/^\/(.+)\/$/, ":" + pt.bookName + "/$1");
      }
      if(tempsa[i].substr(0, 10) == "Wikibooks:") tempsa[i] = ":" + tempsa[i];
      if(tempsa[i].substr(0, 5) == "User:") tempsa[i] = ":" + tempsa[i];
    }
    j = 1;
    temps[0] = tempsa[0];
    for(i = 1; i < tempsa.length; i++) {

      if(tempsa[i] == tempsa[i - 1]) {
        continue;
      } else {
        temps[j++] = tempsa[i];
      }
    }
    temps_len = temps.length;
  }

  wk.spanText(pt.textspan, "Page [[" +
    wk.wikiLinkText(pt.bookName) +
    "]] exists, and contains: <br><ul>" + 
    "<li>Characters: " + len + 
    "<li>Words: " + words +
    "<li>Links: " + links_len + 
    "<li>Categories: " + cats_len + 
    "<li>Templates: " + temps_len +
    "</ul><br><b>Templates:</b><br>");
  for(i = 0; i < temps.length; i++) {
    if(temps[i].substr(0,1) == ':') {
      temps[i] = temps[i].substr(1);
      wk.appendSpanText(pt.textspan, "[[" + wk.wikiLinkText(temps[i]) + "]]<br>");
    } else {
      wk.appendSpanText(pt.textspan, "{{" + wk.wikiLinkText("Template:" + temps[i], temps[i]) + 
                                     "}}<br>");
    }
  }
  wk.appendSpanText(pt.textspan, "<b>Categories:</b><br>");
  for(i = 0; i < cats_len; i++) {
    cats[i] = cats[i].replace("[[", "");
    cats[i] = cats[i].replace("]]", "");
    wk.appendSpanText(pt.textspan, "[[" + wk.wikiLinkText(cats[i]) + "]]<br>");
  }
  wk.appendSpanText(pt.textspan, "<b>Links:</b><br>");
  for(i = 0; i < links_len; i++) {
    links[i] = links[i].replace("[[", "");
    links[i] = links[i].replace("]]", "");
    wk.appendSpanText(pt.textspan, "[[" + wk.wikiLinkText(links[i]) + "]]<br>");
  }
}

*Received education in Taiwan, Canada, and Europe.</br>

