/* Tan slo especifica el host de la wiki, el nombre del usuario y el nombre de archivo */

incluir ("","","toolbarextendido.js");   /*Mdulo de Barra de herramientas */
incluir ("","","enlaceswiki.js");        /*Mdulo de Enlaces Wiki */
incluir ("","","modulobusqueda.js");     /*Mdulo de Bsqueda */
incluir ("","","popups.js");             /*Mdulo de Popups */
