/*
 * Change Special:Search to use a drop-down menu to pick which search engine to use
 * Created by [[w:User:Gracenotes]] for Wikipedia;
 * Updated by [[w:User:Brion VIBBER]] for Wikipedia;
 * Maintained at Wikibooks by [[User:Mike.lifeguard]] and [[User:Darklama]]
 */

function SpecialSearchEnhanced() {
	var	searchEngines = [],
		createOption = function(site, action, mainQ, addQ, addV) {
			searchEngines.push({ action: action, name: mainQ, what: addQ, where: addV});
			return '<option>' + site + '</option>';
		},
		$form = $('#powersearch').add('#search').eq(0),
		$search = $form.find('input[type=search]').eq(0),
		$search_what = $('<select id="searchEngine"/>')
			.append(createOption('English Wikibooks', mw.config.get('wgScript'), 'search', 'title', 'Special:Search'))
			.append(createOption('Google', 'http://www.google.com/search', 'q', 'sitesearch', 'en.wikibooks.org'))
			.append(createOption('Yahoo', 'http://search.yahoo.com/search', 'p', 'vs', 'en.wikibooks.org'))
			.append(createOption('Bing', 'http://www.bing.com/search', 'q', 'q1', 'site:http://en.wikibooks.org'));

	$search.before($search_what);
	$form.submit( { 'search': searchEngines }, function(e) {
		var which = e.data.search[$('#searchEngine').prop('selectedIndex')];

		$search.attr('name', which.name);
		$(this).attr('action', which.action);
		$(this).find('input[name=title]').attr({ name: which.what, value: which.where });
	});
}

if (mw.config.get( 'wgCanonicalSpecialPageName' ) === 'Search') {
	$(SpecialSearchEnhanced);
}

